% Load Data% p_data.mat contains four sets of data stacked on top of each other % Each set is 100 by 20 and each column has 100 random nubers from a % distribution. % We have to find out which set comes from a poisson distribution.% Copy each one into fours different sets %load('p_data.mat');n = 4;for i = 1 : n;data_set{i} = p_data((i - 1)*100 + 1: i*100, :);end;% Calculate mean and variancefor i = 1 : n;for j = 1 : 20;data_set_mean(i,j) = mean(data_set{i}(:,j));data_set_variance(i, j) = var(data_set{i}(:,j));% Calculate Fano Factorsif data_set_mean(i,j) ~= 0fano_factor(i, j) = data_set_variance(i,j)/data_set_mean(i,j);elsefano_factor(i, j) = 0;end;end;% Mean of Fano_factor[row, column, v] = find(fano_factor(i, :));fano_factor_mean(i) = mean(v);end;